import React , {useState} from 'react'

function Counter(props) {
  const {fruits} = props
  const[text,settext] = useState("Enter Fruits Name")
  

  function handleClick(){
    settext("mango")
    console.log({settext})
  }
 
  
  
  return(<>
    <label htmlFor="name">Fruits Name : </label>
    <input type="text" placeholder={text}  id='name' />
    <br /><br />
    <button id='btn' onClick={handleClick}>submit fruits</button>
    <h4>Fruits Are : {fruits} </h4>
    </>
  )
}
export default Counter
